import fs from 'fs';
import { unified } from 'unified';
import { defaultHandlers, mystToDocx } from '../src';
import { u } from 'unist-builder';

const imageBase64Data = ``;

describe('HTML', () => {
  it('serializes a document', async () => {
    const tree = u('root', [
      u('heading', { depth: 1 }, [u('text', { value: 'Welcome to docx' })]),
      u('paragraph', [
        u('text', { value: 'Some ' }),
        u('break'),
        u('emphasis', [u('text', { value: 'italic' })]),
        u('text', { value: ' ' }),
        u('strong', [u('text', { value: 'bold' })]),
        u('text', { value: ' ' }),
        u('underline', [u('text', { value: 'underline' })]),
        u('text', { value: ' ' }),
        u('smallcaps', [u('text', { value: 'MyST' })]),
        u('text', { value: ' ' }),
        u('inlineCode', { value: 'code' }),
        u('text', { value: ' ' }),
        u('link', { url: 'https://curvenote.com' }, [u('text', { value: 'Curvenote' })]),
      ]),
      u('thematicBreak'),
      u('list', { ordered: true }, [
        u('listItem', [u('paragraph', [u('text', { value: 'point 1' })])]),
        u('listItem', [
          u('paragraph', [u('text', { value: 'point 2' })]),
          u('list', { ordered: false }, [
            u('listItem', [u('paragraph', [u('text', { value: 'point 2a' })])]),
            u('listItem', [u('paragraph', [u('text', { value: 'point 2b' })])]),
          ]),
        ]),
      ]),
      u('list', { ordered: false }, [
        u('listItem', [u('paragraph', [u('text', { value: 'point 1' })])]),
        u('listItem', [
          u('paragraph', [u('text', { value: 'point 2' })]),
          u('list', { ordered: false }, [
            u('listItem', [u('paragraph', [u('text', { value: 'point 2a' })])]),
            u('listItem', [u('paragraph', [u('text', { value: 'point 2b' })])]),
          ]),
        ]),
      ]),
      u('blockquote', [u('paragraph', [u('text', { value: 'Do not quote this.' })])]),
      u('paragraph', [u('image', { url: imageBase64Data, width: '30%' })]),
      u('comment', 'hello world'),
    ]);
    const pipe = unified().use(mystToDocx, {
      handlers: defaultHandlers,
      getImageBuffer() {
        return Buffer.from(imageBase64Data, 'base64');
      },
    });
    // await pipe.run(tree as any);
    const file = pipe.stringify(tree as any);
    const buffer = await (file.result as Promise<Buffer>);
    fs.writeFileSync(`hello.docx`, buffer);
    expect(1).toBe(1);
  });
});
